INSERT INTO sections VALUES(2,'text',1,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"heading\",\"attrs\":{\"level\":1},\"content\":[{\"type\":\"text\",\"text\":\"Getting Started\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Headcode CMS is distributed through the shadcn registry, so you can install it directly into your Next.js project using the Shadcn CLI you’re probably familiar with.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Each part of Headcode CMS (themes, section UIs, fields, etc.) is published as its own registry component and can be added using this format:\"}]}]}}"',1,1764170965703,1764170965432);
INSERT INTO sections VALUES(5,'hero',0,0,'"{\"title\":\"A Minimalistic Web CMS\",\"features\":[{\"title\":\"Published as a shadcn registry\"},{\"title\":\"Optimzied for Next.js 16 Cache Components\"},{\"title\":\"Publish your own themes, UI sections, and field types\"},{\"title\":\"Generate layouts and themes with AI tools and agents\"}],\"snippets\":[{\"title\":\"pnpm\",\"code\":\"pnpm dlx shadcn@latest add https://headcodecms.com/r/starter.json\\n(select Next.js 16)\\n\\npnpm drizzle-kit push\\n\\nRemove:\\napp/layout.tsx\\napp/page.tsx\\n\\npnpm dev\\n\\nOpen:\\nWebsite: http://localhost:3000\\nAdmin: http://localhost:3000/headcode\"},{\"title\":\"npm\",\"code\":\"npx shadcn@latest add https://headcodecms.com/r/starter.json\\n(select Next.js 16)\\n\\nnpm drizzle-kit push\\n\\nRemove:\\napp/layout.tsx\\napp/page.tsx\\n\\nnpm run dev\\n\\nOpen:\\nWebsite: http://localhost:3000\\nAdmin: http://localhost:3000/headcode\"}],\"snippetsSublines\":[{\"line\":\"Default: SQLite (file), Better Auth, file storage\"},{\"line\":\"Best for local dev and tryout - no SAAS services required.\"}],\"databases\":[{\"name\":\"SQLite\",\"available\":true},{\"name\":\"Turso Cloud\",\"available\":true},{\"name\":\"Postgres (soon)\",\"available\":false},{\"name\":\"MySQL (soon)\",\"available\":false}],\"storages\":[{\"name\":\"File Storage\",\"available\":true},{\"name\":\"Vercel BLOB\",\"available\":true},{\"name\":\"Uploadthing\",\"available\":true},{\"name\":\"Better Upload (soon)\",\"available\":false}],\"auths\":[{\"name\":\"Better Auth\",\"available\":true},{\"name\":\"Clerk (soon)\",\"available\":false}],\"primaryButton\":{\"title\":\"Getting Started\",\"url\":\"/docs/getting-started\",\"openInNewWindow\":false}}"',3,1764255629494,1764607238261);
INSERT INTO sections VALUES(6,'text',6,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"heading\",\"attrs\":{\"level\":1},\"content\":[{\"type\":\"text\",\"text\":\"Philosophy\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"I built Headcode CMS because I needed something simpler.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Over the years, I’ve worked on many small and mid-sized website projects. Most of them didn’t need a complex headless CMS or a heavy admin interface. They just needed a few content pages, some structured data, and a simple way to manage and preview updates.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"But what I wanted didn’t really exist. At least not in a way that fit naturally into my day-to-day as a frontend developer.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"So, I decided to build it myself.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"I didn’t want to reinvent the wheel. The web ecosystem already has fantastic tools like shadcn/ui, Next.js, Tailwind CSS, TanStack, Drizzle, TipTap, Zod, and many more. What I missed was a lightweight glue between them. Something easy to drop into a project, version-controlled like the rest of the code, and flexible enough to evolve with it.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"So, the core ideas behind Headcode CMS are:\"}]},{\"type\":\"bulletList\",\"content\":[{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"marks\":[{\"type\":\"bold\"}],\"text\":\"Use what already works\"},{\"type\":\"text\",\"text\":\": re-use open source tools and components instead of writing everything from scratch.\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"marks\":[{\"type\":\"bold\"}],\"text\":\"Install it, not host it\"},{\"type\":\"text\",\"text\":\": add it directly to your codebase so you can edit, extend, or remove any part of it.\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"marks\":[{\"type\":\"bold\"}],\"text\":\"Keep it minimal\"},{\"type\":\"text\",\"text\":\": small, clear code that you can actually read and understand.\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"marks\":[{\"type\":\"bold\"}],\"text\":\"Make it fast\"},{\"type\":\"text\",\"text\":\": Next.js 16 Cache Components handle performance.\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"marks\":[{\"type\":\"bold\"}],\"text\":\"Open by design\"},{\"type\":\"text\",\"text\":\": you can create your own shadcn registry to share themes, UI sections, or field types.\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"marks\":[{\"type\":\"bold\"}],\"text\":\"AI as a helper\"},{\"type\":\"text\",\"text\":\": let AI tools help generate layouts or themes when that makes sense.\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"marks\":[{\"type\":\"bold\"}],\"text\":\"Support real workflows\"},{\"type\":\"text\",\"text\":\": draft, review, and publish content the way teams already work.\"}]}]}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"Content Workflow\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Most web projects I’ve worked on share a similar rhythm. A campaign or product launch is coming up. The team creates content and new components on a preview URL. People review it, give feedback, refine, and then it goes live.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"That’s the workflow I had in mind while building Headcode CMS, not an abstract enterprise system, but something that fits naturally into how developers and content people actually collaborate.\"}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"Thank You\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Headcode CMS is an open source project built on many wonderful tools from the community, including Next.js, shadcn/ui, Tailwind CSS, TanStack, Drizzle ORM, Better Auth, TipTap, Zod, and so many more. It exists thanks to the people who create, maintain, and share their work in the open.\"}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"Roadmap\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The first version is published on December 2nd, 2025. It’s still early, and many features are missing. But I want to keep the core small, readable, and easy to maintain.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Here’s what’s next on my list:\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"marks\":[{\"type\":\"bold\"}],\"text\":\"Short-term:\"}]},{\"type\":\"bulletList\",\"content\":[{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Support for more database, storage, and auth providers\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Multi-language support for the admin interface\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"A simple media library\"}]}]}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"marks\":[{\"type\":\"bold\"}],\"text\":\"Long-term:\"}]},{\"type\":\"bulletList\",\"content\":[{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Better integration with AI generation tools\"}]},{\"type\":\"bulletList\",\"content\":[{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"AI app/website builders like v0, Lovable, Bolt, Builder.io are the new theme stores\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The TipTap editor stores rich text content in structured JSONContent format. Therefore all Headcode CMS content is accessible as structured JSON data. Make this available for AI tools\"}]}]}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Support for other tech stacks like TanStack, Laravel Inertia, Shopify Headless, Convex and add support for additional hosting options\"}]}]}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"It’s open source. So if something is missing and you have time or ideas, I’d love to see your contribution.\"}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"How You Can Help\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"You can help by simply using it, reporting issues, suggesting improvements, or sending pull requests. \"},{\"type\":\"text\",\"marks\":[{\"type\":\"link\",\"attrs\":{\"href\":\"https://github.com/headcodecms/headcodecms\",\"target\":\"_blank\",\"rel\":\"noopener noreferrer nofollow\",\"class\":null}}],\"text\":\"Stars on GitHub\"},{\"type\":\"text\",\"text\":\" and kind words on social media really help too (use #headcodecms or mention me directly @headcodecms).\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Headcode CMS is a side project right now, but I’d love to spend more time on it. If you want to support that, I’ve set up a \"},{\"type\":\"text\",\"marks\":[{\"type\":\"link\",\"attrs\":{\"href\":\"https://github.com/sponsors/headcodecms\",\"target\":\"_blank\",\"rel\":\"noopener noreferrer nofollow\",\"class\":null}}],\"text\":\"GitHub Sponsors\"},{\"type\":\"text\",\"text\":\" page. Any support, financial, feedback, or code, means a lot.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Markus\"},{\"type\":\"hardBreak\"},{\"type\":\"text\",\"marks\":[{\"type\":\"link\",\"attrs\":{\"href\":\"mailto:markus@headcodecms.com\",\"target\":\"_blank\",\"rel\":\"noopener noreferrer nofollow\",\"class\":null}}],\"text\":\"markus@headcodecms.com\"},{\"type\":\"hardBreak\"},{\"type\":\"text\",\"marks\":[{\"type\":\"link\",\"attrs\":{\"href\":\"https://mext.at\",\"target\":\"_blank\",\"rel\":\"noopener noreferrer nofollow\",\"class\":null}}],\"text\":\"https://mext.at\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"marks\":[{\"type\":\"italic\"}],\"text\":\"Created with ❤️ in Salzburg, Austria\"}]}]}}"',3,1764257150647,1764607238969);
INSERT INTO sections VALUES(7,'text',1,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Headcode CMS is meant to be customized.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"If you’ve built your own theme, custom field, or integration, or even wrote a tutorial about it, I’d love to see and share it.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"This page will grow as people start sharing what they’ve made. For now, it’s simple: if you’ve created something and want it to be featured here, just send me a message:\"},{\"type\":\"hardBreak\"},{\"type\":\"text\",\"marks\":[{\"type\":\"link\",\"attrs\":{\"href\":\"mailto:markus@headcodecms.com\",\"target\":\"_blank\",\"rel\":\"noopener noreferrer nofollow\",\"class\":null}},{\"type\":\"bold\"}],\"text\":\"markus@headcodecms.com\"}]},{\"type\":\"heading\",\"attrs\":{\"level\":3},\"content\":[{\"type\":\"text\",\"text\":\"Show What You’ve Built\"}]},{\"type\":\"bulletList\",\"content\":[{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"marks\":[{\"type\":\"bold\"}],\"text\":\"Themes\"},{\"type\":\"text\",\"text\":\": Custom designs, layouts, and visual styles built with Tailwind & Shadcn\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"marks\":[{\"type\":\"bold\"}],\"text\":\"Fields\"},{\"type\":\"text\",\"text\":\": Input types or editors you’ve created for specific content needs\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"marks\":[{\"type\":\"bold\"}],\"text\":\"UI Sections\"},{\"type\":\"text\",\"text\":\": Flexible page blocks for your own Shadcn registry\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"marks\":[{\"type\":\"bold\"}],\"text\":\"Other Services\"},{\"type\":\"text\",\"text\":\": Integrations, utilities, or external APIs\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"marks\":[{\"type\":\"bold\"}],\"text\":\"Tutorials, Videos & Articles\"},{\"type\":\"text\",\"text\":\": Anything that helps others learn or get inspired\"}]}]}]},{\"type\":\"heading\",\"attrs\":{\"level\":3},\"content\":[{\"type\":\"text\",\"text\":\"Let’s Grow This Together\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Headcode CMS is still small, and that’s part of the fun. Each contribution, idea, or shared experiment helps shape what it can become.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Don’t wait until it’s perfect. If you made something interesting, send it my way. I’ll start featuring community work here soon.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Markus\"},{\"type\":\"hardBreak\"},{\"type\":\"text\",\"text\":\"markus@headcodecms.com\"}]}]}}"',4,1764262160860,1764262329489);
