INSERT INTO sections VALUES(10,'text',1,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"heading\",\"attrs\":{\"level\":1},\"content\":[{\"type\":\"text\",\"text\":\"Configuration\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Headcode CMS projects are structured around entries, sections, and fields.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"This configuration defines what content exists, how it’s grouped, and how editors interact with it inside the Headcode Admin.\"}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"1. Project Structure\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"A Headcode CMS project consists of:\"}]},{\"type\":\"bulletList\",\"content\":[{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Entries: Identified by a namespace and key.\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Sections: Each entry can include one or multiple sections.\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Fields: Each section defines a set of editable fields.\"}]}]}]},{\"type\":\"heading\",\"attrs\":{\"level\":3},\"content\":[{\"type\":\"text\",\"text\":\"Entries\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"An entry represents a piece of content — for example, a page, blog post, navigation menu, or footer.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Entries can be either static or dynamic:\"}]},{\"type\":\"bulletList\",\"content\":[{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Static entries exist once per project, for example:\"}]},{\"type\":\"bulletList\",\"content\":[{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"namespace: \\\"global\\\", key: \\\"nav\\\" (main navigation)\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"namespace: \\\"global\\\", key: \\\"footer\\\"\"}]}]}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Dynamic entries allow multiple instances within the same namespace, for example\"}]},{\"type\":\"bulletList\",\"content\":[{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"namespace: \\\"blog\\\", key: \\\"post-1\\\", post-2, post-3, etc.\"}]}]}]}]}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"You create dynamic entries directly in the Headcode Admin, while static entries are predefined in the config.\"}]},{\"type\":\"heading\",\"attrs\":{\"level\":3},\"content\":[{\"type\":\"text\",\"text\":\"2. The headcode.config.ts File\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The configuration of entries and sections lives in headcode.config.ts.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Here’s an example configuration file:\"}]}]}}"',5,1764312603637,1764312603305);
