INSERT INTO sections VALUES(12,'text',1,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"heading\",\"attrs\":{\"level\":1},\"content\":[{\"type\":\"text\",\"text\":\"Versioning\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Headcode CMS includes a simple but powerful versioning system.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"It lets you clone all content from an existing version, make changes safely, and prepare new content for an upcoming release, campaign, or website update.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"When combined with Vercel preview deployments, each version can even have its own public preview URL, making it easy for teams to review and approve new content before publishing.\"}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"1. How Versioning Works\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"A Headcode CMS version contains all entries and their sections.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"When you create a new version and clone it from a previous one, all data, entries, sections, and fields, are duplicated.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Each version is defined in your headcode.config.ts file:\"}]},{\"type\":\"paragraph\"}]}}"',6,1764312659861,1764312659769);
INSERT INTO sections VALUES(13,'docs-meta',0,1,'"{\"title\":\"Overview\",\"group\":\"themes\",\"order\":\"100\"}"',7,1764312684769,1764312684444);
INSERT INTO sections VALUES(14,'text',1,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"heading\",\"attrs\":{\"level\":1},\"content\":[{\"type\":\"text\",\"text\":\"Themes Overview\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"A theme in Headcode CMS defines how your website looks and how its content sections are structured.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Each theme contains reusable sections — components such as Hero, Header, Footer, Features, RichText, Image, Image Gallery, or CodeSnippets.\"}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"1. Where Themes Are Installed\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"When you install a theme, it’s added to: \"},{\"type\":\"text\",\"marks\":[{\"type\":\"code\"}],\"text\":\"components/headcode/themes/<theme-name>\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"For example, the Vienna theme (included in the starter kit) is installed at: \"},{\"type\":\"text\",\"marks\":[{\"type\":\"code\"}],\"text\":\"components/headcode/themes/vienna\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Each theme can include multiple sections that can be mixed and matched in your website’s configuration.\"}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"2. Using a Theme in Your Project\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"To enable a theme, register its sections in your \"},{\"type\":\"text\",\"marks\":[{\"type\":\"code\"}],\"text\":\"headcode.config.ts\"},{\"type\":\"text\",\"text\":\" file.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Here’s an example configuration using sections from a theme:\"}]}]}}"',7,1764312706198,1764312705886);
INSERT INTO sections VALUES(15,'docs-meta',0,1,'"{\"title\":\"Build Your Own\",\"group\":\"themes\",\"order\":\"300\"}"',8,1764312770825,1764312770493);
INSERT INTO sections VALUES(16,'text',1,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"heading\",\"attrs\":{\"level\":1},\"content\":[{\"type\":\"text\",\"text\":\"Build Your Own Theme\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"You can create your own theme for your website project, either for personal use or to share it privately or publicly with others.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Each theme consists of a set of sections that define content structure and presentation.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"You can place these sections anywhere in your project, but if you plan to publish the theme, it’s best to organize it under: \"},{\"type\":\"text\",\"marks\":[{\"type\":\"code\"}],\"text\":\"components/headcode/themes/<theme-name>\"}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"1. Creating a Section\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Each section defines how a part of your page looks and what data can be edited in the Headcode Admin.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Here’s a simple example of a hero section:\"}]}]}}"',8,1764312797927,1764312797825);
INSERT INTO sections VALUES(17,'docs-meta',0,1,'"{\"title\":\"Theme Vienna\",\"group\":\"themes\",\"order\":\"200\"}"',9,1764312855615,1764312855181);
INSERT INTO sections VALUES(18,'text',1,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"heading\",\"attrs\":{\"level\":1},\"content\":[{\"type\":\"text\",\"text\":\"Theme Vienna\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The Vienna theme is the default theme installed with Headcode CMS.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"It’s a minimal example theme designed to showcase how Headcode CMS handles sections, fields, and layout composition.\"}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"1. Location\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"After installing Headcode CMS (or the starter kit), the Vienna theme is available at: \"},{\"type\":\"text\",\"marks\":[{\"type\":\"code\"}],\"text\":\"components/headcode/themes/vienna\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"You can browse this folder to see how a theme is structured and how sections are implemented.\"}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"2. Purpose\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Vienna is intentionally simple.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Its goal is to provide a clear reference implementation you can use to:\"}]},{\"type\":\"bulletList\",\"content\":[{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Understand how entries, sections, and fields work together\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Build your own sections and components\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Create your first custom theme or adapt Vienna to your design system\"}]}]}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"It’s a working example you can run and modify, not a full production theme.\"}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"3. Included Sections\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The Vienna theme ships with the following sections:\"}]},{\"type\":\"bulletList\",\"content\":[{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Header: Basic top navigation component\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Hero: Title, subtitle, and primary button section\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Features: Layout for highlighting features or services\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Image: Single image display block\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Text: Rich text section with content editor support\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Footer: Simple footer layout with links and copyright\"}]}]}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Each section includes its own fields and render component, making them easy to customize or reuse in new themes.\"}]},{\"type\":\"paragraph\"}]}}"',9,1764312875377,1764312875284);
