INSERT INTO sections VALUES(20,'text',1,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"heading\",\"attrs\":{\"level\":1},\"content\":[{\"type\":\"text\",\"text\":\"Text Field\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The Text Field is a simple single-line text input field component.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"It’s included by default in the Headcode CMS Starter and uses the Input component from shadcn/ui under the hood (see: \"},{\"type\":\"text\",\"marks\":[{\"type\":\"link\",\"attrs\":{\"href\":\"https://ui.shadcn.com/docs/components/input\",\"target\":\"_blank\",\"rel\":\"noopener noreferrer nofollow\",\"class\":null}}],\"text\":\"https://ui.shadcn.com/docs/components/input).\"}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"1. Installation\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The Text Field is included in the Starter. No additional installation is required.\"}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"2. Basic Usage\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"You can use the TextField in your section definition to create a simple text input.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Example (hero.tsx):\"}]}]}}"',10,1764312992941,1764312992852);
