INSERT INTO sections VALUES(43,'text',9,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"3. Project Structure\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The starter sets up two main routes:\"}]},{\"type\":\"bulletList\",\"content\":[{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Admin: app/(headcode)\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Demo Site: app/(site)\"}]}]}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"To avoid conflicts, remove the default Next.js layout and page files from the app root (these are no longer needed):\"}]},{\"type\":\"bulletList\",\"content\":[{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"app/layout.tsx\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"app/page.tsx\"}]}]}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Note: Keep globals.css — it’s required for shadcn/ui.\"}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"4. Run the Development Server\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"You’re ready to start developing your website with Headcode CMS.\"}]}]}}"',1,1764330350088,1764330349779);
