INSERT INTO sections VALUES(45,'text',11,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Then open your browser:\"}]},{\"type\":\"bulletList\",\"content\":[{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Website: \"},{\"type\":\"text\",\"marks\":[{\"type\":\"link\",\"attrs\":{\"href\":\"http://localhost:3000\",\"target\":\"_blank\",\"rel\":\"noopener noreferrer nofollow\",\"class\":null}}],\"text\":\"http://localhost:3000\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Admin: \"},{\"type\":\"text\",\"marks\":[{\"type\":\"link\",\"attrs\":{\"href\":\"http://localhost:3000/headcode\",\"target\":\"_blank\",\"rel\":\"noopener noreferrer nofollow\",\"class\":null}}],\"text\":\"http://localhost:3000/headcode\"}]}]}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"When you open the admin for the first time, you’ll be prompted to create an administrator user. After that, you can start editing content and customizing your site.\"}]},{\"type\":\"paragraph\"}]}}"',1,1764330535924,1764330535622);
INSERT INTO sections VALUES(46,'code',2,0,'"{\"codes\":[{\"language\":\"ts\",\"filename\":\"headcode.config.ts\",\"code\":\"export const headcodeConfig: HeadcodeConfig = {\\n  version: ''v02'',\\n  clone: ''v01'',\\n  entries: [\\n    {\\n      namespace: ''global'',\\n      key: ''nav'',\\n      sections: [{ section: navSection, pinned: true }],\\n    },\\n    {\\n      namespace: ''global'',\\n      key: ''footer'',\\n      sections: [{ section: footerSection, pinned: true }],\\n    },\\n    {\\n      namespace: ''blog'',\\n      sections: [\\n        { section: metaSection, pinned: true },\\n        { section: heroSection },\\n        { section: textSection },\\n        { section: imageSection },\\n      ],\\n    },\\n  ],\\n};\"}]}"',5,1764434476246,1764434476088);
INSERT INTO sections VALUES(47,'text',3,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"heading\",\"attrs\":{\"level\":3},\"content\":[{\"type\":\"text\",\"text\":\"Configuration Details\"}]},{\"type\":\"bulletList\",\"content\":[{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"marks\":[{\"type\":\"bold\"}],\"text\":\"version\"},{\"type\":\"text\",\"text\":\": The current content version of your project.\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"marks\":[{\"type\":\"bold\"}],\"text\":\"clone\"},{\"type\":\"text\",\"text\":\" (optional): Indicates which version this is cloned from (useful for versioning content).\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"marks\":[{\"type\":\"bold\"}],\"text\":\"pinned sections\"},{\"type\":\"text\",\"text\":\": Sections marked as pinned: true are always included in the entry and cannot be deleted in the admin. Useful for required data, like metadata or summaries.\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"marks\":[{\"type\":\"bold\"}],\"text\":\"dynamic namespaces\"},{\"type\":\"text\",\"text\":\": Namespaces without a key (like blog) are dynamic. Their entries are created in the Headcode Admin.\"}]}]}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"3. Defining Sections\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Sections define reusable content blocks, similar to structured components or slices. Each section defines a name, label, and a set of fields that editors can modify.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Here’s a simple example of a heroSection:\"}]}]}}"',5,1764434649619,1764434649462);
INSERT INTO sections VALUES(48,'code',4,0,'"{\"codes\":[{\"language\":\"tsx\",\"filename\":\"hero.tsx\",\"code\":\"export const heroSection = {\\n  name: ''hero'',\\n  label: ''Hero Section'',\\n  fields: {\\n    title: TextField({\\n      label: ''Title'',\\n    }),\\n    subtitle: TextareaField({\\n      label: ''Subtitle'',\\n    }),\\n    primaryButton: LinkField({\\n      label: ''Primary Button'',\\n    }),\\n  } satisfies Fields,\\n};\\n\\nexport type HeroData = InferSectionData<typeof heroSection.fields>;\"}]}"',5,1764434762989,1764434762841);
INSERT INTO sections VALUES(49,'text',5,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"4. Rendering a Section Component\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"To render the content on your site, parse the section data with the Headcode CMS helper and pass it into your component:\"}]}]}}"',5,1764434874340,1764434874187);
INSERT INTO sections VALUES(50,'code',6,0,'"{\"codes\":[{\"language\":\"tsx\",\"filename\":\"hero.tsx\",\"code\":\"export function Hero({ sectionData }: { sectionData: unknown }) {\\n  const { data } = parseSectionData(heroSection.fields, sectionData);\\n\\n  return (\\n    <div>\\n      <h1>{data.title}</h1>\\n      <p>{data.subtitle}</p>\\n      <Button href={data.primaryButton.url}>\\n        {data.primaryButton.title}\\n      </Button>\\n    </div>\\n  );\\n}\"}]}"',5,1764434917595,1764434917455);
INSERT INTO sections VALUES(52,'code',2,0,'"{\"codes\":[{\"language\":\"ts\",\"filename\":\"headcode.config.ts\",\"code\":\"export const headcodeConfig: HeadcodeConfig = {\\n  version: ''v02'',\\n  clone: ''v01'',\\n  entries: [...],\\n};\"}]}"',6,1764569912308,1764569912013);
INSERT INTO sections VALUES(53,'text',3,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"In this example, a new version v02 is created by cloning everything from version v01.\"}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"2. Database Structure\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Each entry in the database tracks which version it belongs to.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The entries table includes:\"}]},{\"type\":\"bulletList\",\"content\":[{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"id\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"version\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"namespace\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"key\"}]}]}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"3. Typical Versioning Workflow\"}]},{\"type\":\"orderedList\",\"attrs\":{\"start\":1,\"type\":null},\"content\":[{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Create a new Git branch for the new version\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Update your headcode.config.ts to set the new version and clone from the previous one (e.g., version: v02, clone: v01)\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Push the branch to GitHub. Vercel will automatically create a new preview deployment with its own public URL\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Open the Headcode Admin in that preview deployment, you’ll be prompted to clone the content from the previous version\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Edit and prepare the new content in the Admin interface\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Review the preview site and verify everything looks good\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Merge the branch into main, this triggers a new production deployment\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Your new version is now live\"}]}]}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"4. Summary\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Versioning in Headcode CMS is designed to match real‑world workflows.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"It integrates naturally with Git, Vercel, and modern frontend development, giving you a repeatable, safe process for launching new content versions without disrupting your live site.\"}]},{\"type\":\"paragraph\"}]}}"',6,1764569939308,1764569939232);
INSERT INTO sections VALUES(54,'docs-meta',0,1,'"{\"title\":\"Section Editor\",\"group\":\"admin\",\"order\":\"100\"}"',18,1764575797654,1764575797192);
INSERT INTO sections VALUES(55,'text',1,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"heading\",\"attrs\":{\"level\":1},\"content\":[{\"type\":\"text\",\"text\":\"Section Editor\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The section editor allows you to edit the section data as form with the fields you defined in headcode.config.ts.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"An entry has multiple sections. You can add, edit, delete and reorder sections in the sidebar.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The main form allows you to edit the section fields, add items to array fields and delete sections.\"}]},{\"type\":\"paragraph\"}]}}"',18,1764575813755,1764575813535);
INSERT INTO sections VALUES(56,'docs-meta',0,1,'"{\"title\":\"Entries\",\"group\":\"admin\",\"order\":\"200\"}"',19,1764575843142,1764575842698);
INSERT INTO sections VALUES(57,'text',1,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"heading\",\"attrs\":{\"level\":1},\"content\":[{\"type\":\"text\",\"text\":\"Entries\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Entries are the main content units in Headcode CMS.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Each entry is identified by a namespace and a key, and contains one or more sections that define its data.\"}]},{\"type\":\"bulletList\",\"content\":[{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Static entries are defined in \"},{\"type\":\"text\",\"marks\":[{\"type\":\"code\"}],\"text\":\"headcode.config.ts\"},{\"type\":\"text\",\"text\":\". They’re used for unique content like navigation menus, footers, or the homepage.\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Dynamic entries are created in the Headcode Admin. They’re used for repeatable content like blog posts or documentation pages.\"}]}]}]},{\"type\":\"paragraph\"}]}}"',19,1764575858984,1764575858769);
INSERT INTO sections VALUES(58,'docs-meta',0,1,'"{\"title\":\"Users\",\"group\":\"admin\",\"order\":\"300\"}"',20,1764575895910,1764575895438);
INSERT INTO sections VALUES(59,'text',1,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"heading\",\"attrs\":{\"level\":1},\"content\":[{\"type\":\"text\",\"text\":\"Users\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Headcode CMS lets you manage users and roles. By default, there are two roles:\"}]},{\"type\":\"bulletList\",\"content\":[{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Admin: Can manage content, users, and roles.\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"User: Can manage content only.\"}]}]}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"User management is available in the Admin under Users. Authentication and sessions are handled by Better Auth.\"}]},{\"type\":\"paragraph\"}]}}"',20,1764575910421,1764575910210);
INSERT INTO sections VALUES(60,'code',2,0,'"{\"codes\":[{\"language\":\"ts\",\"filename\":\"headcode.config.ts\",\"code\":\"export const headcodeConfig: HeadcodeConfig = {\\n  version: ''v01'',\\n  entries: [\\n    {\\n      namespace: ''global'',\\n      key: ''home'',\\n      sections: [\\n        { section: heroSection },\\n        { section: featuresSection },\\n        { section: textSection },\\n        { section: imageSection },\\n      ],\\n    },\\n    // Other entries...\\n  ],\\n};\\n\"}]}"',7,1764580511039,1764580510885);
INSERT INTO sections VALUES(61,'text',3,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"This defines the homepage (namespace: \\\"global\\\", key: \\\"home\\\") and lists the sections that make it up.\"}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"3. Rendering Sections\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"To render the content for an entry, use the Headcode CMS utilities found in \"},{\"type\":\"text\",\"marks\":[{\"type\":\"code\"}],\"text\":\"lib/headcode/index.ts\"},{\"type\":\"text\",\"text\":\".\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"These functions load and parse entry data, allowing you to render the corresponding section components.\"}]}]}}"',7,1764580531060,1764580530922);
INSERT INTO sections VALUES(62,'code',4,0,'"{\"codes\":[{\"language\":\"tsx\",\"filename\":\"page.tsx\",\"code\":\"export default function Home() {\\n  return <HomeSection />;\\n}\\n\\nasync function HomeSection() {\\n  ''use cache'';\\n\\n  const sections = await getSections(''global'', ''home'');\\n\\n  return sections.map((section) => (\\n    <div key={section.id}>\\n      {section.name === ''hero'' && <Hero sectionData={section.data} />}\\n      {section.name === ''features'' && <Features sectionData={section.data} />}\\n      {section.name === ''text'' && <Text sectionData={section.data} />}\\n      {section.name === ''image'' && <SingleImage sectionData={section.data} />}\\n    </div>\\n  ));\\n}\"}]}"',7,1764580561657,1764580561512);
INSERT INTO sections VALUES(63,'text',5,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Each \"},{\"type\":\"text\",\"marks\":[{\"type\":\"link\",\"attrs\":{\"href\":\"http://section.name\",\"target\":\"_blank\",\"rel\":\"noopener noreferrer nofollow\",\"class\":null}}],\"text\":\"section.name\"},{\"type\":\"text\",\"text\":\" corresponds to the name property in the section definition (e.g., \\\"hero\\\", \\\"text\\\", etc.).\"}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"4. Using Default Section Data\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"During development, you may not have real CMS data yet.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"You can provide default section data to render a complete layout even when no entries exist in the database.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Here’s an example setup with fallback data:\"}]}]}}"',7,1764580662433,1764580662295);
INSERT INTO sections VALUES(64,'code',6,0,'"{\"codes\":[{\"language\":\"tsx\",\"filename\":\"page.tsx\",\"code\":\"export default function Home() {\\n  return <HomeSection />;\\n}\\n\\nasync function HomeSection() {\\n  ''use cache'';\\n\\n  const sections = await getSections(''global'', ''home'');\\n  if (sections.length === 0) {\\n    return (\\n      <div>\\n        <Hero sectionData={defaultHero} />\\n        <Text sectionData={defaultText} />\\n      </div>\\n    );\\n  }\\n\\n  return sections.map((section) => (\\n    <div key={section.id}>\\n      {section.name === ''hero'' && <Hero sectionData={section.data} />}\\n      {section.name === ''features'' && <Features sectionData={section.data} />}\\n      {section.name === ''text'' && <Text sectionData={section.data} />}\\n      {section.name === ''image'' && <SingleImage sectionData={section.data} />}\\n    </div>\\n  ));\\n}\\n\\nconst defaultHero: HeroData = {\\n  title: ''A Minimalistic Web CMS'',\\n  subtitle: ''Published as a shadcn repository'',\\n};\\n\\nconst defaultText: TextData = {\\n  text: {\\n    type: ''doc'',\\n    content: [\\n      {\\n        type: ''paragraph'',\\n        content: [{ type: ''text'', text: ''Lorem ipsum dolor sit amet...'' }],\\n      },\\n    ],\\n  },\\n};\"}]}"',7,1764580684021,1764580683872);
INSERT INTO sections VALUES(65,'code',2,0,'"{\"codes\":[{\"language\":\"tsx\",\"filename\":\"hero.tsx\",\"code\":\"export const heroSection = {\\n  name: ''hero'',\\n  label: ''Hero Section'',\\n  fields: {\\n    title: TextField({\\n      label: ''Title'',\\n    }),\\n    subtitle: TextareaField({\\n      label: ''Subtitle'',\\n    }),\\n    primaryButton: LinkField({\\n      label: ''Primary Button'',\\n    }),\\n  } satisfies Fields,\\n}\\nexport type HeroData = InferSectionData<typeof heroSection.fields>\\n\\nexport function Hero({ sectionData }: { sectionData: unknown }) {\\n  const { data } = parseSectionData(heroSection.fields, sectionData)\\n\\n  return (\\n    <div>\\n      <h1>{data.title}</h1>\\n      <p>{data.subtitle}</p>\\n      <Button href={data.primaryButton.url}>\\n        {data.primaryButton.title}\\n      </Button>\\n    </div>\\n  )\\n}\"}]}"',8,1764582258911,1764582258801);
INSERT INTO sections VALUES(66,'text',3,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"First you create a section definition (\"},{\"type\":\"text\",\"marks\":[{\"type\":\"code\"}],\"text\":\"heroSection\"},{\"type\":\"text\",\"text\":\"):\"}]},{\"type\":\"bulletList\",\"content\":[{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The name uniquely identifies the section.\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The label is shown in the admin UI.\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The fields define editable data (Form fields rendered in Headcode Admin).\"}]}]}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"You can use any available field type (TextField, TextareaField, LinkField, etc.) or create your own custom ones.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Then infer the TypeScript data type for your section using InferSectionData. This gives you full type safety when rendering the section.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"marks\":[{\"type\":\"code\"}],\"text\":\"export type HeroData = InferSectionData<typeof heroSection.fields>;\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Next, create a React component that renders the section on your page:\"}]}]}}"',8,1764582359432,1764582359314);
INSERT INTO sections VALUES(67,'code',4,0,'"{\"codes\":[{\"language\":\"tsx\",\"filename\":\"hero.tsx\",\"code\":\"export function Hero({ sectionData }: { sectionData: unknown }) {\\n  const { data } = parseSectionData(heroSection.fields, sectionData)\\n\\n  return (\\n    <div>\\n      <h1>{data.title}</h1>\\n      <p>{data.subtitle}</p>\\n      <Button href={data.primaryButton.url}>\\n        {data.primaryButton.title}\\n      </Button>\\n    </div>\\n  )\\n}\"}]}"',8,1764582612166,1764582612066);
INSERT INTO sections VALUES(68,'text',5,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"heading\",\"attrs\":{\"level\":3},\"content\":[{\"type\":\"text\",\"text\":\"How It Works\"}]},{\"type\":\"orderedList\",\"attrs\":{\"start\":1,\"type\":null},\"content\":[{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"parseSectionData validates and parses the incoming sectionData object.\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The resulting data object is fully typed as HeroData, giving you autocompletion and type hints in your component.\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Use this data object to render your section contents.\"}]}]}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"3. Publishing Your Theme\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Once your theme works locally, you can publish it so others can install it directly from your registry, just like a shadcn/ui component. Follow the official Shadcn Registry guide:\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"marks\":[{\"type\":\"link\",\"attrs\":{\"href\":\"https://ui.shadcn.com/docs/registry\",\"target\":\"_blank\",\"rel\":\"noopener noreferrer nofollow\",\"class\":null}}],\"text\":\"https://ui.shadcn.com/docs/registry\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"There you’ll learn how to create a registry entry for your theme and publish it privately or publicly.\"}]},{\"type\":\"heading\",\"attrs\":{\"level\":3},\"content\":[{\"type\":\"text\",\"text\":\"Your Own Theme Store\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"If you’re building multiple themes or want to share them with your team or clients, you can create your own theme store by hosting your own shadcn registry.\"}]},{\"type\":\"paragraph\"}]}}"',8,1764582644705,1764582644604);
INSERT INTO sections VALUES(69,'code',2,0,'"{\"codes\":[{\"language\":\"tsx\",\"filename\":\"hero.tsx\",\"code\":\"import { TextField } from ''@/components/headcode/form/text-field''\\n\\nexport const heroSection = {\\n  name: ''hero'',\\n  label: ''Hero Section'',\\n  fields: {\\n    title: TextField({\\n      label: ''Title'',\\n    }),\\n    // other fields...\\n  },\\n};\"}]}"',10,1764586982379,1764586982308);
INSERT INTO sections VALUES(70,'text',3,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"In the Headcode Admin, this will render a labeled text input field named title within the Hero Section. The entered value is stored and available as part of the section’s data object.\"}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"3. Options\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The TextField function accepts several optional parameters for customizing its behavior.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Example:\"}]}]}}"',10,1764587008611,1764587008544);
INSERT INTO sections VALUES(71,'code',4,0,'"{\"codes\":[{\"language\":\"tsx\",\"filename\":\"hero.tsx\",\"code\":\"export const heroSection = {\\n  name: ''hero'',\\n  label: ''Hero Section'',\\n  fields: {\\n    title: TextField({\\n      label: ''Title'',\\n      defaultValue: ''My Title'',\\n      validator: z.string().min(3).max(100),\\n    }),\\n  },\\n};\"}]}"',10,1764587054134,1764587054066);
