INSERT INTO sections VALUES(73,'code',6,0,'"{\"codes\":[{\"language\":\"tsx\",\"filename\":\"hero.tsx\",\"code\":\"export function Hero({ sectionData }: { sectionData: unknown }) {\\n  const { data } = parseSectionData(heroSection.fields, sectionData);\\n\\n  return (\\n    <div>\\n      <h1>{data.title}</h1>\\n    </div>\\n  );\\n}\"}]}"',10,1764587219545,1764587219482);
INSERT INTO sections VALUES(74,'text',7,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"5. Sections Without Rendering Components\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Not every section needs to render something on the page. You can also create meta sections, sections that store structured data such as metadata, SEO information, author details, or campaign settings.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Meta sections usually define fields but don’t include a React component. They’re useful for storing non-visual information that can be accessed programmatically when rendering or processing content.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"These sections are often pinned in your \"},{\"type\":\"text\",\"marks\":[{\"type\":\"code\"}],\"text\":\"headcode.config.ts\"},{\"type\":\"text\",\"text\":\", ensuring they can’t be removed from an entry.\"}]}]}}"',7,1764587828644,1764587828581);
