INSERT INTO sections VALUES(76,'text',3,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The Section Editor is the main editing interface in Headcode CMS Admin.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"It allows you to manage and edit the data for each section defined in your \"},{\"type\":\"text\",\"marks\":[{\"type\":\"code\"}],\"text\":\"headcode.config.ts\"},{\"type\":\"text\",\"text\":\" file.\"}]}]}}"',18,1764601072735,1764601072664);
