INSERT INTO sections VALUES(135,'text',1,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"heading\",\"attrs\":{\"level\":1},\"content\":[{\"type\":\"text\",\"text\":\"Switch\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The Switch Field represents a true/false toggle option. It uses the \"},{\"type\":\"text\",\"marks\":[{\"type\":\"link\",\"attrs\":{\"href\":\"https://ui.shadcn.com/docs/components/switch\",\"target\":\"_blank\",\"rel\":\"noopener noreferrer nofollow\",\"class\":null}}],\"text\":\"Switch\"},{\"type\":\"text\",\"text\":\" from shadcn/ui.\"}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"Installation\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Included in Headcode CMS Starter, no setup needed.\"}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"Usage\"}]},{\"type\":\"paragraph\"}]}}"',29,1764672039946,1764672039874);
INSERT INTO sections VALUES(136,'code',2,0,'"{\"codes\":[{\"language\":\"tsx\",\"filename\":\"featureSection.tsx\",\"code\":\"export const featureSection = {\\n  name: ''feature'',\\n  label: ''Feature Toggle'',\\n  fields: {\\n    enabled: SwitchField({\\n      label: ''Enabled'',\\n      defaultValue: true,\\n    }),\\n  },\\n};\"}]}"',29,1764672086156,1764672086088);
