INSERT INTO sections VALUES(138,'text',1,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"heading\",\"attrs\":{\"level\":1},\"content\":[{\"type\":\"text\",\"text\":\"Build Your Own Fields\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"You can create your own fields for your website project, either for personal use or to share it privately or publicly with others.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"To get started, check some of the existing fields in Headcode CMS — like TextField, TextareaField, or ImageField — and use them as inspiration for your own custom fields.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Custom fields allow you to extend Headcode CMS with your own input types, validations, and admin UI components.\"}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"Structure\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Each field consists of two parts:\"}]},{\"type\":\"bulletList\",\"content\":[{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Field definition: defines the structure, type, and validation (e.g., text-field.ts)\"}]}]},{\"type\":\"listItem\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Field component: defines how the field is rendered and edited inside the Headcode Admin (e.g., text-field-component.tsx)\"}]}]}]},{\"type\":\"heading\",\"attrs\":{\"level\":2},\"content\":[{\"type\":\"text\",\"text\":\"Examples\"}]},{\"type\":\"paragraph\"}]}}"',30,1764672433813,1764672433727);
INSERT INTO sections VALUES(139,'code',2,0,'"{\"codes\":[{\"language\":\"ts\",\"filename\":\"text-field.ts\",\"code\":\"import { lazy } from ''react''\\nimport { z } from ''zod''\\nimport type { FieldProps } from ''@/lib/headcode/types''\\n\\nconst DefaultTextField: FieldProps<string, unknown> = {\\n  label: ''Text Field'',\\n  component: lazy(() => import(''./text-field-component'')),\\n  defaultValue: '''',\\n  validator: z.string(),\\n}\\nexport const TextField = (params: Partial<FieldProps<string, unknown>>) => ({\\n  ...DefaultTextField,\\n  ...params,\\n})\"}]}"',30,1764672836049,1764672835975);
INSERT INTO sections VALUES(140,'code',3,0,'"{\"codes\":[{\"language\":\"tsx\",\"filename\":\"text-field-component.tsx\",\"code\":\"import {\\n  Field,\\n  FieldDescription,\\n  FieldError,\\n  FieldLabel,\\n} from ''@/components/ui/field''\\nimport { Input } from ''@/components/ui/input''\\nimport { useFieldContext } from ''./app-form''\\n\\nexport default function TextFieldComponent({\\n  label,\\n  description,\\n}: {\\n  label: string\\n  description?: string | undefined\\n}) {\\n  const field = useFieldContext<string>()\\n  const isInvalid = field.state.meta.isTouched && !field.state.meta.isValid\\n  return (\\n    <Field data-invalid={isInvalid}>\\n      <FieldLabel htmlFor={field.name}>{label}</FieldLabel>\\n      <Input\\n        id={field.name}\\n        name={field.name}\\n        value={field.state.value}\\n        onBlur={field.handleBlur}\\n        onChange={(e) => field.handleChange(e.target.value)}\\n        aria-invalid={isInvalid}\\n        autoComplete=\\\"off\\\"\\n      />\\n      {description && <FieldDescription>{description}</FieldDescription>}\\n      {isInvalid && <FieldError errors={field.state.meta.errors} />}\\n    </Field>\\n  )\\n}\"}]}"',30,1764672961041,1764672960970);
INSERT INTO sections VALUES(141,'text',4,0,'"{\"text\":{\"type\":\"doc\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Note, that with \"},{\"type\":\"text\",\"marks\":[{\"type\":\"code\"}],\"text\":\"const field = useFieldContext<string>()\"},{\"type\":\"text\",\"text\":\" you get the TanStack field context.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The Link Field combines multiple fields into a new field type:\"}]}]}}"',30,1764672981174,1764672981102);
INSERT INTO sections VALUES(142,'code',5,0,'"{\"codes\":[{\"language\":\"ts\",\"filename\":\"link-field.ts\",\"code\":\"import { lazy, type ComponentType } from ''react''\\nimport { z } from ''zod''\\nimport type { FieldProps } from ''@/lib/headcode/types''\\n\\nexport type LinkValue = {\\n  title: string\\n  url: string\\n  openInNewWindow: boolean\\n}\\n\\nconst DefaultLinkField: FieldProps<LinkValue, unknown> = {\\n  label: ''Link Field'',\\n  component: lazy(() => import(''./link-field-component'')) as ComponentType<{\\n    label: string\\n    description?: string\\n    options?: unknown\\n  }>,\\n  defaultValue: {\\n    title: '''',\\n    url: '''',\\n    openInNewWindow: false,\\n  },\\n  validator: z.object({\\n    title: z.string(),\\n    url: z.string(),\\n    openInNewWindow: z.boolean().default(false),\\n  }),\\n}\\nexport const LinkField = (params: Partial<FieldProps<LinkValue, unknown>>) => ({\\n  ...DefaultLinkField,\\n  ...params,\\n})\"}]}"',30,1764673117268,1764673117187);
INSERT INTO sections VALUES(143,'code',6,0,'"{\"codes\":[{\"language\":\"tsx\",\"filename\":\"link-field-component.tsx\",\"code\":\"import {\\n  Field,\\n  FieldDescription,\\n  FieldError,\\n  FieldLabel,\\n} from ''@/components/ui/field''\\nimport { Input } from ''@/components/ui/input''\\nimport { Checkbox } from ''@/components/ui/checkbox''\\nimport { useFieldContext } from ''./app-form''\\nimport type { LinkValue } from ''./link-field''\\n\\nexport default function LinkFieldComponent({\\n  label,\\n  description,\\n}: {\\n  label: string\\n  description?: string | undefined\\n}) {\\n  const field = useFieldContext<LinkValue>()\\n  const isInvalid = field.state.meta.isTouched && !field.state.meta.isValid\\n  const value = field.state.value || {\\n    title: '''',\\n    url: '''',\\n    openInNewWindow: false,\\n  }\\n\\n  const handleTitleChange = (title: string) => {\\n    field.handleChange({\\n      ...value,\\n      title,\\n    })\\n  }\\n\\n  const handleUrlChange = (url: string) => {\\n    field.handleChange({\\n      ...value,\\n      url,\\n    })\\n  }\\n\\n  const handleOpenInNewWindowChange = (openInNewWindow: boolean) => {\\n    field.handleChange({\\n      ...value,\\n      openInNewWindow,\\n    })\\n  }\\n\\n  return (\\n    <Field data-invalid={isInvalid}>\\n      {isInvalid && <FieldError errors={field.state.meta.errors} />}\\n      <div className=\\\"space-y-4\\\">\\n        <div className=\\\"flex flex-col gap-4 md:flex-row md:gap-4\\\">\\n          <div className=\\\"flex-1\\\">\\n            <FieldLabel htmlFor={`${field.name}-title`} className=\\\"text-sm\\\">\\n              {label}\\n            </FieldLabel>\\n            <Input\\n              id={`${field.name}-title`}\\n              name={`${field.name}.title`}\\n              value={value.title}\\n              onBlur={field.handleBlur}\\n              onChange={(e) => handleTitleChange(e.target.value)}\\n              aria-invalid={isInvalid}\\n              autoComplete=\\\"off\\\"\\n              className=\\\"mt-1\\\"\\n            />\\n          </div>\\n          <div className=\\\"flex-1\\\">\\n            <FieldLabel htmlFor={`${field.name}-url`} className=\\\"text-sm\\\">\\n              URL\\n            </FieldLabel>\\n            <Input\\n              id={`${field.name}-url`}\\n              name={`${field.name}.url`}\\n              value={value.url}\\n              onBlur={field.handleBlur}\\n              onChange={(e) => handleUrlChange(e.target.value)}\\n              aria-invalid={isInvalid}\\n              autoComplete=\\\"off\\\"\\n              className=\\\"mt-1\\\"\\n            />\\n          </div>\\n        </div>\\n        <div className=\\\"flex items-center gap-2\\\">\\n          <Checkbox\\n            id={`${field.name}-openInNewWindow`}\\n            name={`${field.name}.openInNewWindow`}\\n            checked={value.openInNewWindow}\\n            onCheckedChange={(checked) =>\\n              handleOpenInNewWindowChange(checked === true)\\n            }\\n            onBlur={field.handleBlur}\\n            aria-invalid={isInvalid}\\n          />\\n          <FieldLabel\\n            htmlFor={`${field.name}-openInNewWindow`}\\n            className=\\\"cursor-pointer text-sm font-normal\\\"\\n          >\\n            Open in new window\\n          </FieldLabel>\\n        </div>\\n      </div>\\n      {description && <FieldDescription>{description}</FieldDescription>}\\n    </Field>\\n  )\\n}\"}]}"',30,1764673146848,1764673146771);
